import Slide1 from "./slide_1.png";
import Slide2 from "./slide_2.png";
import Slide3 from "./slide_3.png";
import App from './Hook';

import "../css/Slide.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faUser, faCartShopping, faAngleRight, faShoppingBag, faHeart, faExpand, faLocationArrow, faPhone, faEnvelope} from '@fortawesome/free-solid-svg-icons';


