</script>

const Slide = () => {
    return (
        <div>
            <div className="list">
            <div className="item" id="item_0">
            <img src= {Slide1} alt=""  className = "image_cover"/>
            </div>
           

            <div className="item" id="item_1">
            <img src= {Slide2} alt=""  className = "image_cover"/>
            </div>

            <div className="item" id="item_2">
            <img src= {Slide3} alt=""  className = "image_cover"/>
            </div>

            </div>
        <button id="next"><FontAwesomeIcon icon={faAngleRight} /></button>
        </div>
    );
}

export default Slide;
