import React from 'react';
import "../css/Card.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faShoppingBag, faHeart, faExpand} from '@fortawesome/free-solid-svg-icons';

const Card = (props) => {
    return (
             <div class="card">
                <div class="card-body">
                 <img src = {props.image} class = "img1"/>
                 <div class = "action-product">
                  <a href="#" class = "action tooltip" ><FontAwesomeIcon icon={faShoppingBag} />
                    <span class = "tooltiptext">Thêm vào giỏ hàng</span>
                  </a>
                  <a href="#" class = "action tooltip"><FontAwesomeIcon icon={faHeart} />
                    <span class = "tooltiptext">Thêm vào mục ưa thích</span>
                  </a>
                  <a href="#" class = "action tooltip"><FontAwesomeIcon icon={faExpand} />
                    <span class = "tooltiptext">Xem Chi Tiết</span>
                  </a>
                </div>
                 <h3>{props.nameProduct}</h3>
                  <a href="#" class="btn">{props.price}</a>
                </div>
              </div>
    );
}

export default Card;
